import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { createPost } from '../action/postActions';


class AddNewEleve extends Component {
  constructor(props){
    super(props);    
    this.state = {
      nom: '',
      prenom: "",
      age:null,
      classe: null
    };

    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
}

onChange(e) {
  this.setState({ [e.target.name]: e.target.value });
}

onSubmit(e) {
  e.preventDefault();
  const post = {
    nom: this.state.nom,
    prenom: this.state.prenom,
    age: this.state.age,
    classe: this.state.classe,
  };

  this.props.createPost(post);
  this.state.nom = '';
  this.state.prenom = '';
  this.state.age = '';
  this.state.classe = '';
}
    render() {
      return (
          <div>
              <form onSubmit={this.onSubmit} >
                <div>
                  <span>Nom:</span> <input type="text" name="nom" value={this.state.nom} 
                  onChange={this.onChange} /> <br/>
                  <span>Prenom:</span> <input type="text" name="prenom" value={this.state.prenom} 
                  onChange={this.onChange} /> <br/>
                  <span>Age:</span> <input type="text" name="age" value={this.state.age} 
                  onChange={this.onChange} /> <br/>
                  <span>Classe:</span> <input type="text" name="classe" value={this.state.classe} 
                  onChange={this.onChange} /> <br/>
                  <button type="submit">Poster</button>        
                </div>
            </form>
          </div>
      );
    }
  }


AddNewEleve.propTypes = {
  createPost: PropTypes.func.isRequired
};

export default connect(
  null,
  { createPost }
)(AddNewEleve);
