export const CHANGE_NOM = 'CHANGE_NOM'
export const CHANGE_PRENOM = 'CHANGE_PRENOM'
export const CHANGE_AGE = 'CHANGE_AGE'
export const CHANGE_CLASSE = 'CHANGE_CLASSE'
export const INITIALIZE_FORM = 'INITIALIZE_FORM'
export const REQUEST_DATA = 'REQUEST_DATA'
export const RECEIVE_DATA_SUCCESS = 'RECEIVE_DATA_SUCCESS'
export const RECEIVE_DATA_FAILED = 'RECEIVE_DATA_FAILED'

export const changeNom = nom => ({
    type: CHANGE_NOM,
    nom,
})
export const changePrenom = prenom => ({
    type: CHANGE_AGE,
    prenom,
})
export const changeAge = age => ({
    type: CHANGE_AGE,
    age,
})
export const changeClasse = classe => ({
    type: CHANGE_CLASSE,
    classe,
})
export const initializeForm = () => ({
    type: INITIALIZE_FORM,
})
export const requestData = () => ({
    type: REQUEST_DATA,
})
export const receiveDataSuccess = characterArray => ({
    type: RECEIVE_DATA_SUCCESS,
    characterArray,
})
export const receiveDataFailed = () => ({
    type: RECEIVE_DATA_FAILED,
})
